#include <stdio.h>
int power(int base,int pwr){int num=base;for(int i=1;i<pwr;i++){num=num*base;}return num;}constintbase=2;long long int toBin(int dec){long long int num=0;int pw=0;while(power(base,pw)<=dec/2){pw++;}for(int i=pw;i>-1;i--){num*=10;if(power(base,i)<=dec){dec-=power(base,i);num++;}}return num;}int main(){int decimal;printf("\nInt to convert :");scanf("%d",&decimal);printf("\n%lld\n\n",toBin(decimal));}
